function add(t,n){return t+n}function multiply(t,n){return t*n}function subtract(t,n){return t-n}function divide(t,n){return t/n}function calculateStuff(t){var n=t.split(/\+|-|\*|\//),e=parseInt(n[0]),i=t.match(/\+|-|\*|\//),r=parseInt(n[1]);switch(i[0]){case"+":result=add(e,r);break;case"-":result=subtract(e,r);break;case"*":result=multiply(e,r);break;case"/":result=divide(e,r);break;case null:console.log("do nothing");break;default:console.log("something isn't going right!")}display.innerHTML=result}function displayButtonInput(t){display.innerHTML+=t}for(var inputString="",result=0,display=document.getElementById("display"),btnNumber=document.getElementsByClassName("btn-number"),i=0;i<btnNumber.length;i++)btnNumber[i].addEventListener("click",function(){"="===inputString?(display.innerHTML="",inputString=this.innerHTML,displayButtonInput(this.innerHTML)):(inputString+=this.innerHTML,displayButtonInput(this.innerHTML))});for(var btnOperator=document.getElementsByClassName("btn-operator"),i=0;i<btnOperator.length;i++)btnOperator[i].addEventListener("click",function(){inputString+=this.innerHTML,displayButtonInput(this.innerHTML)});var btnEquals=document.getElementById("btn-equals");btnEquals.addEventListener("click",function(){calculateStuff(inputString),inputString="="});var btnCancel=document.getElementById("btn-cancel");btnCancel.addEventListener("click",function(){inputString="",display.innerHTML=""});